head.script:
scwin.onpageload = function () {
    grpBA.hide();
    grpAAScreen.hide();
    grpAATR.hide();
    grpDA.hide();
    grpMember.hide();

    btnFilterALL.setStyle("font-weight", "bold");
    btnFilterALL.setStyle("border-bottom-width", "2px");
};

scwin.searchboxOK_onkeypress = function (e) {
    scwin.requestSearch();
};

scwin.searchboxOK_onchange = function () {
    scwin.requestSearch();
};

scwin.searchDetail = function (rowIndex, columnIndex, columnId, type) {
    // $p.log(rowIndex + columnId + columnId);

    var cellData = "";
    if (type === 0) {
        cellData = unitTasks.getCellData(rowIndex, columnId);
    } else if (type === 1) {
        cellData = dataList3.getCellData(rowIndex, columnId);
    } else if (type === 2) {
        cellData = dataList4.getCellData(rowIndex, columnId);
    } else if (type === 3) {
        cellData = dataList5.getCellData(rowIndex, columnId);
    } else if (type === 4) {
        cellData = emps.getCellData(rowIndex, columnId);
    }

    $p.log(cellData);

    var rowJSON = {
        "Personal Info": { "Name": "Lee", "Age": 50, "Married": false },
        "Region": "Seoul",
        "Carrier": true,
        "name": cellData
    };

    // 새로 열릴 화면에 데이터를 전달 (dataObject를 통해 전달 가능)
    var obj = {
        "dataObject": {
            "type": "json",
            "name": "LeftSearch",
            "data": rowJSON
        }
    };

    scwin.setContentsSrc(type, obj);
};

scwin.setContentsSrc = async function (type, obj) {
    if (type === 0) {
        await $p.parent().wfmRightContents.setSrc("/page/component/search/SystemSearchResultBizDetail.xml", obj);
    } else if (type === 1) {
        await $p.parent().wfmRightContents.setSrc("/page/component/search/SystemSearchResultScreenDetail.xml", obj);
    } else if (type === 2) {
        await $p.parent().wfmRightContents.setSrc("/page/component/search/SystemSearchResultTRDetail.xml", obj);
    } else if (type === 3) {
        await $p.parent().wfmRightContents.setSrc("/page/component/search/SystemSearchResultDBDetail.xml", obj);
    } else if (type === 4) {
        await $p.parent().wfmRightContents.setSrc("/page/component/search/SystemSearchResultMemberDetail.xml", obj);
    }
};

scwin.requestSearch = function () {

    const baParam = {
        offset: 0,
        limit: 13,
        type: "PAGE",
        sortMethod: "NO",
        sortFields: ["unitAfrsName"],
        searchField: "unitAfrsName",
        searchContent: searchboxOK.getValue()
    };

    $c.sbm.execute(sbmUnitTaskSearchList, baParam);


    // const screenParam = {
    //     offset: 0,
    //     limit: 13,
    //     type: "PAGE",
    //     sortMethod: "NO",
    //     sortFields: ["depName"],
    //     searchField: "empName",
    //     searchContent: searchboxOK.getValue()
    // };

    // $c.sbm.execute(sbmEmpSearchList, screenParam);


    // const trParam = {
    //     offset: 0,
    //     limit: 13,
    //     type: "PAGE",
    //     sortMethod: "NO",
    //     sortFields: ["depName"],
    //     searchField: "empName",
    //     searchContent: searchboxOK.getValue()
    // };

    // $c.sbm.execute(sbmEmpSearchList, trParam);


    // const dbParam = {
    //     offset: 0,
    //     limit: 13,
    //     type: "PAGE",
    //     sortMethod: "NO",
    //     sortFields: ["depName"],
    //     searchField: "empName",
    //     searchContent: searchboxOK.getValue()
    // };

    // $c.sbm.execute(sbmEmpSearchList, dbParam);


    const memberParam = {
        offset: 0,
        limit: 13,
        type: "PAGE",
        sortMethod: "NO",
        sortFields: ["empName"],
        searchField: "empName",
        searchContent: searchboxOK.getValue()
    };

    $c.sbm.execute(sbmEmpSearchList, memberParam);

    // 엔터치면 코드 적용
    if (searchboxOK.getValue() == "") {
        grpBA.hide();
        grpAAScreen.hide();
        grpAATR.hide();
        grpDA.hide();
        grpMember.hide();
    } else if ((e.charCode || e.keyCode) === 13) {
        // $p.parent().wfmRightContents.setSrc("/page/component/SystemSearchResultDetail.xml");
        unitTasks.clearFilter();
        dataList3.clearFilter();
        dataList4.clearFilter();
        dataList5.clearFilter();
        emps.clearFilter();

        let jsonFilterOptions = {};

        jsonFilterOptions.type = "row";
        jsonFilterOptions.colIndex = "name";
        jsonFilterOptions.key = searchboxOK.getValue();
        jsonFilterOptions.exactMatch = false;
        jsonFilterOptions.condition = "and";

        dataList3.setColumnFilter(jsonFilterOptions);
        dataList4.setColumnFilter(jsonFilterOptions);
        dataList5.setColumnFilter(jsonFilterOptions);

        if (dataList3.getRowCount() <= 0) {
            grpAAScreen.hide();
        } else {
            grpAAScreen.show("");
        }

        if (dataList4.getRowCount() <= 0) {
            grpAATR.hide();
        } else {
            grpAATR.show("");
        }

        if (dataList5.getRowCount() <= 0) {
            grpDA.hide();
        } else {
            grpDA.show("");
        }
    } else {
        // $p.log("SEARCH!!!!");

        // 검색을 구현하면 될듯
        // dataList1.clearFilter();

        // let jsonFilterOptions = {};

        // jsonFilterOptions.type = "row";
        // jsonFilterOptions.colIndex = "name";
        // jsonFilterOptions.key = searchboxOK.getValue();
        // jsonFilterOptions.exactMatch = false;
        // jsonFilterOptions.condition = "and";

        // dataList1.setColumnFilter(jsonFilterOptions);        
    }

};

