};

// TR 검색
scwin.gridAATRSearchResult_oncellclick = function (rowIndex, columnIndex, columnId) {
    scwin.searchDetail(rowIndex, columnIndex, columnId, 2);
};

// DB 검색
scwin.gridDADBSearchResult_oncellclick = function (rowIndex, columnIndex, columnId) {
    scwin.searchDetail(rowIndex, columnIndex, columnId, 3);
};

// 담당자 검색
scwin.gridMemberSearchResult_oncellclick = function (rowIndex, columnIndex, columnId) {
    scwin.searchDetail(rowIndex, columnIndex, columnId, 4);
};

scwin.resetBtnFontWeight = function () {
    btnFilterALL.setStyle("font-weight", "");
    btnFilterBA.setStyle("font-weight", "");
    btnFilterAAScreen.setStyle("font-weight", "");
    btnFilterAATR.setStyle("font-weight", "");
    btnFilterDA.setStyle("font-weight", "");
    btnFilterMember.setStyle("font-weight", "");

    btnFilterALL.setStyle("border-bottom-width", "0px");
    btnFilterBA.setStyle("border-bottom-width", "0px");
    btnFilterAAScreen.setStyle("border-bottom-width", "0px");
    btnFilterAATR.setStyle("border-bottom-width", "0px");
    btnFilterDA.setStyle("border-bottom-width", "0px");
    btnFilterMember.setStyle("border-bottom-width", "0px");
}

scwin.btnFilterALL_onclick = function (e) {
    scwin.resetBtnFontWeight();
    btnFilterALL.setStyle("font-weight", "bold");
    btnFilterALL.setStyle("border-bottom-width", "2px");

    if (searchboxOK.getValue() == "") {
        grpBA.hide();
        grpAAScreen.hide();
        grpAATR.hide();
        grpDA.hide();
        grpMember.hide();
    } else {

        if (unitTasks.getRowCount() > 0) {
            grpBA.show("");
        } else {
            grpBA.hide();
        }

        if (dataList3.getRowCount() > 0) {
            grpAAScreen.show("");
        } else {
            grpAAScreen.hide();
        }

        if (dataList4.getRowCount() > 0) {
            grpAATR.show("");
        } else {
            grpAATR.hide();
        }

        if (dataList5.getRowCount() > 0) {
            grpDA.show("");
        } else {
            grpDA.hide();
        }

        if (emps.getRowCount() > 0) {
            grpMember.show("");
        } else {
            grpMember.hide();
        }

    }
};

